const snippetSchema = new Schema({
  title: String,
  description: String,
  language: String,
  date_added: Date,
  date_updated: Date,
  favorite: Boolean,
  snippet: String,
});

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer components {
  .h1 {
    @apply font-bold text-2xl mb-4;
  }

  .wrapper {
    @apply absolute left-80 top-0 w-[calc(100%-320px)] h-full;
  }

  .wrapper-inner {
    @apply absolute h-full w-[970px] bg-white top-5 left-1/2 -translate-x-1/2 p-10 shadow-md;
  }

  .grey-border {
    @apply border-solid border-slate-300 border rounded-sm;
  }
}
